import React from "react";
function From(){
    return ( 
        <div>
            <form>
                <Control type='text' label='Email'/> <br/>
                <Control type='password' label='Password' /> <br/>
                {/* <input type="checkbox" /> */}
                <Paragraph type='danger'>Lorem ipsum dolor sit amet consectetur, adipisicing elit. Doloribus non saepe quae!</Paragraph>
            </form>
        </div>
    )
}


function Control(props){
    return (
        <div className="form-group">
                <label className="form-label">{props.label} </label>
                <input className="form-control" type={props.type} placeholder={props.label} />
        </div>

    )
}

function Paragraph(props){
    if(props.type === 'danger')
    {
        return 
        (<p className="text-danger">{props.children}</p>)
    
    }
    
    if(props.type === 'warning')
    return 
        (<p className="text-warning">{props.children}</p>)
    
        return
        <p className="text-secondary">{props.children}</p>
         
}

export default From;
